import React from "react";
import Navbar from "./Navbar";
import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import Header from "./Header";
import { useParams } from "react-router-dom";
import "./Clothing.css";
function Description() {
  const params = useParams();
  const { id } = params;
  console.log(id, "id");
  const [apiArray, setApiArray] = useState([]);
  const [apiArrayDuplicate, setApiArrayDuplicate] = useState([]);
  const [apiArrayDuplicate1, setApiArrayDuplicate1] = useState([]);
  const [heading, setHeading] = useState("");

  const [ele, setEle] = useState([]);
  useEffect(()=>{

    const fetchApi=async ()=>{
      const res =  await fetch("https://fakestoreapi.com/products")
      const data = await res.json();
      console.log(data,'api');
      setApiArray(data)
      setApiArrayDuplicate(data);
    }
    fetchApi()
    elementFilter();
  },[])


  const transfer = () => {
    console.log("transer");
  };

  const filtering = (CATEGORY) => {
    const UpdatedList = apiArray.filter((dataEach) => {
      return dataEach.category === CATEGORY;
    });

    console.log("inside filterinh");
    setApiArrayDuplicate(UpdatedList);
    setHeading(CATEGORY);
    console.log(apiArrayDuplicate);
  };


  const elementFilter = () => {
    const updatedLIst1 = apiArray.filter((each) => {
      return each.id === id;
    });
    console.log(updatedLIst1,'upl');
    setEle(updatedLIst1);

  };
  const Navigation = () => {
    const Navigate = useNavigate();
    Navigate("/description");
    console.log("function");
  };
  console.log(apiArray, "iiiii");

  return (
    <>
      <Header />
      <Navbar
        apiDataArray={apiArray}
        heading={heading}
        filtering={filtering}
        apiArrayDuplicate={apiArrayDuplicate}
      />

      {ele.map((each) => {
        return (
          <>
            <h2>{each.id}</h2>
          </>
        );
      })}

      <hr />
    </>
  );
}
export default Description;
