import React, { useState,useEffect } from "react";
import Navbar from "./Navbar";
import Element from "./Element";
import { BrowserRouter,Route,Routes } from "react-router-dom";
import './Clothing.css';




function Clothing()
{
  const [apiArray,setApiArray]=useState([]);
  const [apiArrayDuplicate,setApiArrayDuplicate]=useState([]);
   const [heading,setHeading]=useState('');




  useEffect(()=>{

    const fetchApi=async ()=>{
      const res =  await fetch("https://fakestoreapi.com/products")
      const data = await res.json();
      console.log(data)
      setApiArray(data)
      setApiArrayDuplicate(data);
    }
    fetchApi()
  },[])

  const filtering=(CATEGORY)=>
  {
     const UpdatedList=apiArray.filter(
         (dataEach)=>{
             return dataEach.category===CATEGORY;
         }

     )
     console.log('inside filterinh');
     setApiArrayDuplicate(UpdatedList)
     setHeading(CATEGORY);
     console.log(apiArrayDuplicate);
  }
  return(

    <>
    <BrowserRouter>
    <Routes>
      <Route element='Element' path='/description' ></Route>
    </Routes>
    </BrowserRouter>
    <div className='logo'>
    <img src={process.env.PUBLIC_URL + 'img/logo.png'}  style={{width:'70px',display:"inline",marginTop:"-8px"}}/> <br/><br/>
     <h2 style={{display:'inline',cursor:"pointer"}}> Y'Z PRODUCTS..</h2>
     </div>
     <hr/>
     < Navbar apiDataArray={apiArray} heading={heading} filtering={filtering} apiArrayDuplicate={apiArrayDuplicate}/>
     <hr/>
     <h3 style={{color:'rED',textAlign:'center',cursor:"pointer"}}><u>{heading.toUpperCase()}</u></h3>
     <Element apiDataArray={apiArray}  apiArrayDuplicate={apiArrayDuplicate}/>
     </>
  )
}

export default Clothing;
