import { useFormik } from "formik";
import { signUpSchema } from "../schemas";

function Form()
{
    const initialValues={
        name:'',
        email:'',
        confirm_password:'',
        password:''
    }
    const {values,errors,handleChange,handleBlur,handleSubmit,touched}=useFormik({
        initialValues:initialValues,
        validationSchema:signUpSchema,
        onSubmit:(values,action)=>(console.log(values), action.resetForm())
    }

    )
    console.log(errors);


    return(
        <>
        <form onSubmit={handleSubmit}>
            <input type='text' name='name' id='name' onChange={handleChange} onBlur={handleBlur}/><br/><br/>
            <input type='text' name='email' id='email' onChange={handleChange} onBlur={handleBlur}/><br/><br/>
            <input type='password' name='password' id='password' onChange={handleChange} onBlur={handleBlur} /><br/><br/>
            <input type='password' name='confirm_password' id='confirm_password' onChange={handleChange} onBlur={handleBlur} /><br/><br/>
            <input type='submit' onSubmit={handleSubmit}/>

        </form>
        </>
    )
}
export default Form;
