import React from "react";
import Element from "./Element";

function Navbar({apiDataArray})
{
    const [arrList,setArrList]=useState(apiDataArray);
    const categoryArr=[...new Set(apiDataArray.map((data)=>{return data.category}))]
    const filtering=(data)=>
    {
        arrList.filter((currElem)=>{
            return currElem.category === data;



        })

        setArrList(arrList);

    }

return(
    <>
    {
       categoryArr.map((data)=>
       <>
       <button onClick={filtering({data})}>{data}</button>
       </>
       ) 
    }
    </>
)

}

export default Navbar;
