import React, { useState } from "react";

function Postreq1()
{
    const [id,setId]=useState();
    const  [title,setTitle]=useState("");
    const [body,setBody]=useState("");
    const addData=()=>{
        const params={
            method:'post',
            headers:{'Content-Type':'application/json'},
            body:JSON.stringify({id,title,body}),

        }
        fetch('https://jsonplaceholder.typicode.com/posts',params).then(res=>res.json()).then(()=>console.log('response'+id+title+body));

    }

return (
    <>
    <h2>POST REQUEST</h2>
    ID<input type="text" value={id} onChange={(e)=>setId(e.target.value)}/><br/><br/>
    TITLE<input type="text" value={title} onChange={(e)=>setTitle(e.target.value)}/><br/><br/>
    BODY<input type="text" value={body} onChange={(e)=>setBody(e.target.value)}/><br/><br/>
    <input type="button" value="add" onClick={()=>addData()}/>
    </>
)

}
export default Postreq1;
