import React, { useState } from "react";

function Element({apiDataArray,apiArrayDuplicate})
{
        return(
            <>
        {
            apiArrayDuplicate.map((user)=>{
             const {image,price,id,category,description,title}=user;
             return(
                <div key={id} className='elementBox'>
                    <img  className='elementImg'src={image}/>
                    <div className="elementBoxContent">
                    <h2 style={{color:'rgb(227, 71, 43)',alignContent:"center"}}><u>{title}</u></h2>
                    <br/><br/>
                    <h6 className="boxDescription">{description}</h6>
                    <h4>PRICE:{price}</h4>

                    </div>
                    </div>
             )
            })
        }
        </>
        )
}
export default Element;



